    private void jButton2MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton2MouseClicked
        // TODO add your handling code here:
         System.out.println("Running on Chrome");
			System.out.println("Clearing Cache");
			System.out.println("Executing Test Cases");
        try {
            Runtime.getRuntime().exec("C:\\Program Files (x86)\\Google\\Chrome\\Application\\chrome.exe http://localhost/GIT/Trunk/ui/test/mpower-test.html");
        } catch (IOException ex) {
            Logger.getLogger(Create_Test.class.getName()).log(Level.SEVERE, null, ex);
        }
        try {
            TimeUnit.SECONDS.sleep(2);
        } catch (InterruptedException ex) {
            Logger.getLogger(Create_Test.class.getName()).log(Level.SEVERE, null, ex);
        }
			//Clearing Cache
			Robot robot1;
			try {
				robot1 = new Robot();
				robot1.keyPress(KeyEvent.VK_SHIFT);
				robot1.keyPress(KeyEvent.VK_CONTROL);
				robot1.keyPress(KeyEvent.VK_R);
				robot1.keyRelease(KeyEvent.VK_SHIFT);
				robot1.keyRelease(KeyEvent.VK_CONTROL);
				robot1.keyRelease(KeyEvent.VK_R);
			} catch (AWTException e) {
				// TODO Auto-generated catch block
				e.printStackTrace();
			}
    }//GEN-LAST:event_jButton2MouseClicked

    private void saveMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_saveMouseClicked
        BufferedWriter writer3=null;
        try {
            // TODO add your handling code here:
            String input1=codeurl.getText();
            writer3 = new BufferedWriter(new FileWriter(input1));
            String line = code.getText();
            writer3.write(line);
            writer3.newLine();
            writer3.close();
        } catch (IOException ex) {
            Logger.getLogger(Create_Test.class.getName()).log(Level.SEVERE, null, ex);
        } finally {
            try {
                writer3.close();
            } catch (IOException ex) {
                Logger.getLogger(Create_Test.class.getName()).log(Level.SEVERE, null, ex);
            }
        }
    }//GEN-LAST:event_saveMouseClicked

